import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open settings by clicking on the settings button
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForLoadState('networkidle');

    // Take screenshot after opening settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_opened_settings.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_opened_settings.png'));

    // Navigate to Appearance tab
    await page.waitForSelector('.tab-btn[data-tab="appearance"]');
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigating to Appearance
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_navigated_to_appearance.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_navigated_to_appearance.png'));

    // Change theme to Light
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', 'light');

    // Take screenshot after changing theme
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_changed_theme_to_light.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_changed_theme_to_light.png'));

    // Verify that the theme has been changed to Light
    const selectedTheme = await page.$eval('#theme-select', el => el.value);

    return {
      success: true,
      action: "Changed theme to Light in Appearance settings",
      extractedData: { theme: selectedTheme },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
