import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Perform 20 different search queries in rapid succession
    const searchQueries = [
      'latest news',
      'technology trends',
      'web development tips',
      'AI advancements',
      'health and wellness',
      'travel destinations',
      'cooking recipes',
      'fitness routines',
      'financial advice',
      'movie reviews',
      'book recommendations',
      'music playlists',
      'gaming news',
      'environmental issues',
      'fashion trends',
      'home improvement',
      'parenting tips',
      'self-improvement',
      'photography techniques',
      'sports updates',
      'local events'
    ];

    for (const query of searchQueries) {
      await page.fill('#search-input', query); // Fill the search input
      await page.waitForTimeout(500); // Wait a bit for the search to process
      await page.screenshot({ path: path.join(screenshotsDir, `search_query_${query.replace(/\s+/g, '_')}.png`) }); // Take a screenshot of the search results
      screenshots.push(path.join(screenshotsDir, `search_query_${query.replace(/\s+/g, '_')}.png`));
      await page.fill('#search-input', ''); // Clear the search input for the next query
    }

    return {
      success: true,
      action: "Performed 20 different search queries to stress the search system",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

