import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggested accounts to load
    await page.waitForSelector('.account-item:first-child .follow-btn');

    // Click the Follow button on the first suggested account
    await page.click('.account-item:first-child .follow-btn');

    // Take a screenshot after clicking the Follow button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    return {
      success: true,
      action: "Clicked Follow button on the first suggested account",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the sidebar to load and the suggested accounts to become visible
    await page.waitForSelector('.sidebar', { timeout: 5000 });
    await page.waitForSelector('.account-item:first-child .follow-btn', { timeout: 5000 });

    // Click the Follow button on the first suggested account
    const followButtonSelector = '.account-item:first-child .follow-btn';
    await page.click(followButtonSelector);

    // Take a screenshot after clicking the Follow button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    // Verify if the follow action was successful by checking if the button text has changed (example logic)
    const followButtonText = await page.textContent(followButtonSelector);
    const isFollowing = followButtonText.includes('Following');

    return {
      success: true,
      action: "Clicked Follow button on the first suggested account",
      extractedData: { isFollowing },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

