import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread input field to be visible and type 'Hello World!'
    await page.waitForSelector('#thread-text');
    await page.fill('#thread-text', 'Hello World!');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typing.png'));

    // Wait for the Post button to be visible and click it
    await page.waitForSelector('#post-thread');
    await page.click('#post-thread');

    // Take screenshot after posting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_posted.png'));

    // Verify that the post was successful by checking for the presence of the new thread in the feed
    const postExists = await page.evaluate(() => {
      return document.querySelector('.threads-feed')?.innerText.includes('Hello World!');
    });

    return {
      success: postExists,
      action: "Typed 'Hello World!' in the thread input and clicked the Post button",
      extractedData: { postExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

