import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread text area to appear and type 'Hello World!'
    await page.waitForSelector('#thread-text', { timeout: 5000 });
    await page.fill('#thread-text', 'Hello World!');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_text_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_text_entered.png'));

    // Wait for the Post button and click it
    await page.waitForSelector('#post-thread', { timeout: 5000 });
    await page.click('#post-thread');

    // Take screenshot after posting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_posted.png'));

    // Verify that the post has been made (you may want to check for a specific element that appears)
    const postExists = await page.$eval('.threads-feed', el => el.innerText.includes('Hello World!'));

    return {
      success: true,
      action: "Posted 'Hello World!' to the thread",
      extractedData: { postExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
