import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggested accounts to load
    await page.waitForSelector('.account-item:first-child .follow-btn');

    // Click the Follow button on the first suggested account
    await page.click('.account-item:first-child .follow-btn');

    // Take a screenshot after clicking the Follow button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    // Verify if the follow action was successful
    const followButtonState = await page.$eval('.account-item:first-child .follow-btn', button => button.textContent);

    return {
      success: true,
      action: "Clicked Follow button on the first suggested account",
      extractedData: { followButtonState },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

