import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Extract thread text content and metadata
    const threads = await page.evaluate(() => {
      const threadElements = document.querySelectorAll('.thread-item'); // Assuming each thread has a class 'thread-item'
      return Array.from(threadElements).map(thread => {
        const textContent = thread.querySelector('.thread-text')?.innerText || '';
        const timestamp = thread.querySelector('.thread-time')?.innerText || '';
        return { textContent, timestamp };
      });
    });

    // Log extracted threads data
    console.log(threads);

    // Take screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata",
      extractedData: threads,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

