import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread text area to be visible
    await page.waitForSelector('#thread-text');

    // Type in the textarea
    const inputText = 'Testing character count functionality';
    await page.fill('#thread-text', inputText);

    // Wait for the character count to update
    await page.waitForSelector('#char-count');

    // Extract the character count
    const charCountText = await page.innerText('#char-count');
    const charCount = charCountText.split('/')[0].trim();

    // Take a screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    return {
      success: true,
      action: "Typed text and verified character count updates",
      extractedData: { charCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

