import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Type in the thread text area
    const threadTextAreaSelector = '#thread-text';
    await page.waitForSelector(threadTextAreaSelector);
    await page.fill(threadTextAreaSelector, 'Testing character count functionality');

    // Wait for character count to update
    const charCountSelector = '#char-count';
    await page.waitForSelector(charCountSelector);
    const charCountText = await page.textContent(charCountSelector);

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Extract character count for verification
    const charCountValue = charCountText.match(/(\d+)\/(\d+)/);
    const currentCharCount = charCountValue ? charCountValue[1] : null;

    return {
      success: true,
      action: "Typed text and verified character count updates",
      extractedData: { currentCharCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
