import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first thread item to be visible
    await page.waitForSelector('.threads-feed .thread-item:first-child');

    // Click the bookmark button on the first thread
    const bookmarkButtonSelector = '.threads-feed .thread-item:first-child .thread-action[data-action="bookmark"]';
    await page.waitForSelector(bookmarkButtonSelector);
    await page.click(bookmarkButtonSelector);

    // Take a screenshot after clicking the bookmark button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmark_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    // Verify if the bookmark action was successful
    const isBookmarked = await page.evaluate(() => {
      const firstThread = document.querySelector('.threads-feed .thread-item:first-child');
      return firstThread ? firstThread.querySelector('.thread-action[data-action="bookmark"].active') !== null : false;
    });

    return {
      success: true,
      action: "Clicked the bookmark button on the first thread in the feed",
      extractedData: { isBookmarked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
