import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search view to be displayed
    await page.waitForSelector('#search-view');

    // Click on the 'Hashtags' tab to switch the search category
    await page.waitForSelector("button.tab-btn[data-tab='hashtags']");
    await page.click("button.tab-btn[data-tab='hashtags']");

    // Take screenshot after switching to 'Hashtags' tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hashtags_tab.png'));

    // Extract meaningful data to verify task completion
    const activeTab = await page.evaluate(() => {
      return document.querySelector('button.tab-btn.active').textContent;
    });

    return {
      success: true,
      action: "Switched to 'Hashtags' tab in search view",
      extractedData: { activeTab },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
