import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type 'jun'
    await page.waitForSelector('#search-input');
    await page.fill('#search-input', 'jun');

    // Take screenshot after typing in search input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Wait for the People tab to be visible and click it
    await page.waitForSelector("button.tab-btn[data-tab='people']");
    await page.click("button.tab-btn[data-tab='people']");

    // Take screenshot after switching to People tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_people_tab.png'));

    return {
      success: true,
      action: "Searched for 'jun' and switched to People tab",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

