import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to the search view
    await page.click('li[data-view="search"]'); // Using another selector strategy
    await page.waitForSelector('#search-view'); // Wait for the search view to load

    // Type 'jun' into the search input field
    await page.fill('#search-input', 'jun');

    // Take screenshot after typing in the search input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Switch to the People tab
    await page.click('.tab-btn[data-tab="people"]'); // Using class and data attribute selector
    await page.waitForSelector('.tab-btn.active'); // Wait for the active class to ensure the tab is selected

    // Take screenshot after switching to People tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_people_tab.png'));

    return {
      success: true,
      action: "Navigated to search view, typed 'jun' in search input, and switched to People tab.",
      extractedData: {
        searchInputValue: await page.inputValue('#search-input'), // Extracting the value from the search input
        activeTab: await page.evaluate(() => document.querySelector('.tab-btn.active').innerText) // Extracting active tab text
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
