import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Type text into the thread input field
    const threadInputSelector = '#thread-text';
    await page.waitForSelector(threadInputSelector);
    await page.fill(threadInputSelector, 'Testing character count functionality');

    // Verify character count updates
    const charCountSelector = '#char-count';
    await page.waitForSelector(charCountSelector);
    const charCountText = await page.innerText(charCountSelector);

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Extract meaningful data to verify task completion
    const charCountValue = charCountText.split('/')[0].trim(); // Extract the current character count
    const expectedCharCount = '30'; // Expected character count after typing the text

    return {
      success: charCountValue === expectedCharCount,
      action: "Typed text and verified character count updates",
      extractedData: { charCount: charCountValue },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

