import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the Search navigation item to switch to Search view
    await page.waitForSelector(".nav-item[data-view='search']");
    await page.click(".nav-item[data-view='search']");
    await page.waitForLoadState('networkidle');

    // Take screenshot after clicking on Search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click_search.png'));

    // Verify that the Search view is active
    const isSearchViewActive = await page.isVisible("#search-view.active");

    return {
      success: true,
      action: "Switched from Home view to Search view",
      extractedData: { isSearchViewActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

