import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the Search navigation item to switch to Search view
    await page.waitForSelector('.nav-item[data-view="search"]', { timeout: 5000 });
    await page.click('.nav-item[data-view="search"]');

    // Take screenshot after clicking search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click_search.png'));

    // Verify that we are on the Search view
    const isSearchViewActive = await page.evaluate(() => {
      return document.querySelector('.nav-item[data-view="search"]').classList.contains('active');
    });

    return {
      success: true,
      action: "Switched from Home view to Search view",
      extractedData: { isSearchViewActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
