import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the Activity navigation item
    await page.waitForSelector('.nav-item[data-view="activity"]');
    await page.click('.nav-item[data-view="activity"]');

    // Wait for the activity feed to load
    await page.waitForSelector('#activity-feed'); // Assuming there's an element with this ID in the activity view

    // Take screenshot after clicking Activity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_activity_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_activity_feed.png'));

    return {
      success: true,
      action: "Clicked on Activity navigation item to view activity feed",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

