import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Activity navigation item to be visible and click it
    await page.waitForSelector('.nav-item[data-view="activity"]', { state: 'visible' });
    await page.click('.nav-item[data-view="activity"]');

    // Wait for the activity feed to load
    await page.waitForSelector('#activity-feed', { state: 'visible' });

    // Take screenshot of the activity feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_activity_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_activity_feed.png'));

    // Return the result
    return {
      success: true,
      action: "Clicked on Activity navigation item and viewed activity feed",
      extractedData: {
        activityFeedVisible: await page.isVisible('#activity-feed')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
