import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first thread to be visible
    await page.waitForSelector('.thread-item:first-child .thread-action[data-action="like"]');

    // Click the like button on the first thread
    await page.click('.thread-item:first-child .thread-action[data-action="like"]');

    // Take a screenshot after clicking the like button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_like_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_like_clicked.png'));

    // Verify if the like action was successful (you might need to adjust this based on the actual implementation)
    const isLiked = await page.evaluate(() => {
      const likeButton = document.querySelector('.thread-item:first-child .thread-action[data-action="like"]');
      return likeButton && likeButton.classList.contains('liked'); // Assuming 'liked' class indicates a successful like
    });

    return {
      success: true,
      action: "Clicked the like button on the first thread",
      extractedData: { isLiked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

