import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.threads-feed');

    // Select all visible like buttons
    const likeButtons = await page.$$eval(".thread-action[data-action='like']", buttons => {
      return buttons.map(button => button.closest('.thread-item'));
    });

    // Click each like button rapidly
    for (const thread of likeButtons) {
      const likeButton = await thread.$(".thread-action[data-action='like']");
      if (likeButton) {
        await likeButton.click();
        await page.waitForTimeout(50); // brief pause to avoid overwhelming the server
      }
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_liking.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_liking.png'));

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
