import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first thread item to load
    await page.waitForSelector('.thread-item:first-child');

    // Click on the bookmark button of the first thread
    const bookmarkButtonSelector = '.thread-item:first-child .thread-action[data-action="bookmark"]';
    await page.waitForSelector(bookmarkButtonSelector);
    await page.click(bookmarkButtonSelector);

    // Take screenshot after clicking the bookmark button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmark_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    // Extract confirmation message or check if the action was successful
    const successMessage = await page.evaluate(() => {
      const toast = document.querySelector('.toast-success');
      return toast ? toast.innerText : null;
    });

    return {
      success: true,
      action: "Clicked the bookmark button on the first thread in the feed",
      extractedData: { successMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
