import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread input field and type 'Hello World!'
    await page.waitForSelector('#thread-text');
    await page.fill('#thread-text', 'Hello World!');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Wait for the Post button and click it
    await page.waitForSelector('#post-thread');
    await page.click('#post-thread');

    // Take screenshot after posting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_posting.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_posting.png'));

    // Extract confirmation or result data
    const confirmationMessage = await page.locator('.toast-message').innerText();

    return {
      success: true,
      action: "Posted 'Hello World!' in the thread",
      extractedData: { confirmationMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

