import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggested accounts to load
    await page.waitForSelector('.account-item:first-child .follow-btn');

    // Click the Follow button on the first suggested account
    await page.click('.account-item:first-child .follow-btn');

    // Take a screenshot after the action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    // Extract confirmation of follow action
    const followStatus = await page.evaluate(() => {
      const followButton = document.querySelector('.account-item:first-child .follow-btn');
      return followButton ? followButton.textContent : null;
    });

    return {
      success: true,
      action: "Clicked Follow button on the first suggested account",
      extractedData: { followStatus },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

