import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggested accounts to load
    await page.waitForSelector('.account-item');

    // Click the Follow button on the first suggested account
    const followButtonSelector = '.account-item:first-child .follow-btn';
    await page.waitForSelector(followButtonSelector);
    await page.click(followButtonSelector);

    // Take a screenshot after clicking follow
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_follow.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_follow.png'));

    // Extract data to verify task completion
    const firstSuggestedAccount = await page.$eval('.account-item:first-child', el => ({
      username: el.querySelector('.username').innerText,
      isFollowing: el.querySelector('.follow-btn').innerText.includes('Following')
    }));

    return {
      success: true,
      action: "Clicked Follow button on the first suggested account",
      extractedData: firstSuggestedAccount,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
