import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Extract usernames from the threads feed
    const usernames = await page.evaluate(() => {
      const usernameElements = document.querySelectorAll('.thread-username');
      return Array.from(usernameElements).map(el => el.textContent.trim());
    });

    // Log the extracted usernames
    console.log(usernames);

    // Take a screenshot after extracting usernames
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_usernames_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_usernames_extracted.png'));

    return {
      success: true,
      action: "Collected all usernames visible in the current thread feed",
      extractedData: { usernames },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

