import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search view to be visible
    await page.waitForSelector('#search-view');

    // Click on the 'Hashtags' tab to switch search category
    await page.click("button.tab-btn[data-tab='hashtags']");
    await page.waitForTimeout(1000); // Wait for the content to load

    // Take screenshot after clicking the 'Hashtags' tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hashtags_tab.png'));

    // Extract meaningful data to verify task completion
    const activeTab = await page.evaluate(() => {
      return document.querySelector('button.tab-btn.active').innerText;
    });

    return {
      success: true,
      action: "Switched to 'Hashtags' tab in search view",
      extractedData: { activeTab },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

