import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Rapidly open and close the reply modal 10 times
    for (let i = 0; i < 10; i++) {
      // Click to open the reply modal
      await page.waitForSelector(".thread-action[data-action='reply']");
      await page.click(".thread-action[data-action='reply']");
      await page.waitForSelector("#close-reply-modal", { state: 'visible' });

      // Take screenshot after opening the modal
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_open_modal_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_open_modal_${i + 1}.png`));

      // Click to close the reply modal
      await page.click("#close-reply-modal");
      await page.waitForSelector(".thread-action[data-action='reply']", { state: 'visible' });

      // Take screenshot after closing the modal
      await page.screenshot({ path: path.join(screenshotsDir, `step_3_close_modal_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_3_close_modal_${i + 1}.png`));
    }

    return {
      success: true,
      action: "Rapidly opened and closed the reply modal 10 times",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

