import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first thread item to be visible
    await page.waitForSelector('.thread-item:first-child .thread-action[data-action="like"]');

    // Click the like button on the first thread
    await page.click('.thread-item:first-child .thread-action[data-action="like"]');

    // Take a screenshot after clicking the like button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_like_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_like_clicked.png'));

    // Extract data to verify the like action was successful
    const likeButton = await page.$('.thread-item:first-child .thread-action[data-action="like"]');
    const isLiked = await likeButton.evaluate(el => el.classList.contains('liked'));

    return {
      success: true,
      action: "Clicked the like button on the first thread",
      extractedData: { isLiked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
