      const likeButton = document.querySelector(selector);
      return likeButton && likeButton.classList.contains('liked'); // Assuming 'liked' class indicates a liked state
    }, likeButtonSelector);

    return {
      success: isLiked,
      action: isLiked ? "Successfully liked the first thread" : "Failed to like the first thread",
      extractedData: { liked: isLiked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
