import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the Activity navigation item to view activity feed
    await page.waitForSelector('.nav-item[data-view="activity"]');
    await page.click('.nav-item[data-view="activity"]');

    // Wait for the activity feed to load
    await page.waitForSelector('.activity-feed'); // Assuming there's a selector for the activity feed

    // Take a screenshot after clicking the Activity item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_activity_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_activity_feed.png'));

    // Extract meaningful data to verify task completion
    const activityData = await page.evaluate(() => {
      // Assuming activity feed has a specific structure
      return Array.from(document.querySelectorAll('.activity-item')).map(item => ({
        content: item.innerText,
        timestamp: item.querySelector('.timestamp')?.innerText || ''
      }));
    });

    return {
      success: true,
      action: "Clicked on Activity navigation item to view activity feed",
      extractedData: { activityData },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

