import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Click like buttons on all visible threads
    const likeButtons = await page.$$('.thread-action[data-action="like"]');
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // Wait for a short duration between clicks
    }

    // Take a screenshot after liking the threads
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_liking.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_liking.png'));

    return {
      success: true,
      action: "Liked all visible threads",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.threads-feed');

    // Select all visible like buttons
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click each like button in rapid succession
    for (const button of likeButtons) {
      await button.click();
      // Optional: Add a small delay between clicks to mimic human interaction
      await page.waitForTimeout(100);
    }

    // Take a final screenshot after clicking like buttons
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_likes_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_likes_clicked.png'));

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {
        likedThreadsCount: likeButtons.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

