import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search view to be visible
    await page.waitForSelector('#search-view', { visible: true });

    // Click on the 'Hashtags' tab to switch the search category
    await page.waitForSelector("button.tab-btn[data-tab='hashtags']", { visible: true });
    await page.click("button.tab-btn[data-tab='hashtags']");

    // Take screenshot after clicking the 'Hashtags' tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hashtags_tab.png'));

    // Verify that the 'Hashtags' tab is active
    const isActive = await page.evaluate(() => {
      const activeTab = document.querySelector('button.tab-btn.active');
      return activeTab && activeTab.dataset.tab === 'hashtags';
    });

    return {
      success: isActive,
      action: "Switched to 'Hashtags' search category",
      extractedData: { isActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
