import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the first video container with data-video-id="1" to be visible
    const containerSelector = 'div.video-container[data-video-id="1"]';
    await page.waitForSelector(containerSelector, { state: 'visible' });

    // Wait for the play/pause button inside that container
    // Use a descendant CSS selector for precision and robustness
    const playPauseBtnSelector = `${containerSelector} button.play-pause-btn`;
    await page.waitForSelector(playPauseBtnSelector, { state: 'visible' });

    // Extra check: make sure the button is enabled and clickable
    const playPauseBtn = await page.$(playPauseBtnSelector);
    if (!playPauseBtn) throw new Error('Play/Pause button not found in video container with data-video-id="1"');

    // Optionally, extract the current icon state (play or pause) before click
    const iconClass = await page.evaluate(btn => {
      const icon = btn.querySelector('i');
      return icon ? icon.className : '';
    }, playPauseBtn);

    // Click the play/pause button
    await playPauseBtn.click();

    // Wait briefly to allow UI to update (in case there's animation)
    await page.waitForTimeout(500);

    // Take screenshot after clicking play/pause
    const afterClickShot = path.join(screenshotsDir, 'step_2_after_playpause.png');
    await page.screenshot({ path: afterClickShot });
    screenshots.push(afterClickShot);

    // Extract the state of the play/pause button after click (icon class)
    const iconClassAfter = await page.evaluate(sel => {
      const btn = document.querySelector(sel);
      if (!btn) return null;
      const icon = btn.querySelector('i');
      return icon ? icon.className : '';
    }, playPauseBtnSelector);

    return {
      success: true,
      action: 'Clicked the play/pause button of the first video (data-video-id=1).',
      extractedData: {
        beforeClickIconClass: iconClass,
        afterClickIconClass: iconClassAfter
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
