import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container with data-video-id="1" to be visible
    const videoContainerSelector = '.video-container[data-video-id="1"]';
    await page.waitForSelector(videoContainerSelector, { state: 'visible', timeout: 5000 });

    // Wait for the play/pause button inside the first video container
    const playPauseBtnSelector = `${videoContainerSelector} .play-pause-btn`;
    await page.waitForSelector(playPauseBtnSelector, { state: 'visible', timeout: 5000 });

    // Take screenshot before click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button
    await page.click(playPauseBtnSelector);

    // Wait a moment for UI update (icon may change)
    await page.waitForTimeout(700);

    // Take screenshot after click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract data: check if the play/pause icon changed (fa-pause vs fa-play)
    const iconClass = await page.$eval(
      `${playPauseBtnSelector} i`,
      el => el.className
    );

    // Determine state based on icon
    let playPauseState = null;
    if (iconClass.includes('fa-play')) {
      playPauseState = 'paused';
    } else if (iconClass.includes('fa-pause')) {
      playPauseState = 'playing';
    } else {
      playPauseState = 'unknown';
    }

    return {
      success: true,
      action: "Clicked the play/pause button of the first video (data-video-id='1')",
      extractedData: {
        playPauseIconClass: iconClass,
        playPauseState
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

