export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the first video container with data-video-id="1"
    const videoContainerSelector = 'div.video-container[data-video-id="1"]';
    await page.waitForSelector(videoContainerSelector, { state: 'visible', timeout: 5000 });

    // Alternative selector for the play/pause button inside the first video container
    // Using a more specific CSS path and also XPath as a backup
    const playPauseBtnSelector = `${videoContainerSelector} button.play-pause-btn`;

    // Wait for the play/pause button to be visible
    await page.waitForSelector(playPauseBtnSelector, { state: 'visible', timeout: 3000 });

    // Scroll the play/pause button into view to avoid overlay issues
    await page.evaluate((sel) => {
      const btn = document.querySelector(sel);
      if (btn) btn.scrollIntoView({ behavior: 'instant', block: 'center' });
    }, playPauseBtnSelector);

    // Take screenshot before click
    const beforeClickShot = path.join(screenshotsDir, 'step_2_before_click.png');
    await page.screenshot({ path: beforeClickShot });
    screenshots.push(beforeClickShot);

    // Click the play/pause button using CSS selector
    await page.click(playPauseBtnSelector);

    // Wait briefly to allow UI to update (e.g., icon changes)
    await page.waitForTimeout(800);

    // Take screenshot after click
    const afterClickShot = path.join(screenshotsDir, 'step_3_after_click.png');
    await page.screenshot({ path: afterClickShot });
    screenshots.push(afterClickShot);

    // Extract info: check if the icon within the button changed (pause/play)
    const iconClass = await page.evaluate((sel) => {
      const btn = document.querySelector(sel);
      if (!btn) return null;
      const icon = btn.querySelector('i');
      return icon ? icon.className : null;
    }, playPauseBtnSelector);

    return {
      success: true,
      action: "Clicked the play/pause button of the first video (data-video-id='1')",
      extractedData: {
        playPauseButtonIconClass: iconClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

