import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the first video's container using attribute selector
    const videoContainerSelector = "div.video-container[data-video-id='1']";
    await page.waitForSelector(videoContainerSelector, { state: 'visible', timeout: 5000 });

    // Find the play/pause button inside the first video container, using a combination of selectors
    const playPauseBtnSelector =
      "div.video-container[data-video-id='1'] .control-buttons .play-pause-btn";
    await page.waitForSelector(playPauseBtnSelector, { state: 'visible', timeout: 5000 });

    // Take screenshot before clicking
    const beforeClickShot = path.join(screenshotsDir, 'step_2_before_click.png');
    await page.screenshot({ path: beforeClickShot });
    screenshots.push(beforeClickShot);

    // Click the play/pause button
    const playPauseBtn = await page.$(playPauseBtnSelector);
    if (!playPauseBtn) throw new Error("Play/Pause button not found in video container 1");
    await playPauseBtn.click();

    // Wait a short moment for any UI change
    await page.waitForTimeout(800);

    // Take screenshot after clicking
    const afterClickShot = path.join(screenshotsDir, 'step_3_after_click.png');
    await page.screenshot({ path: afterClickShot });
    screenshots.push(afterClickShot);

    // Extract button icon state to verify if it toggled (fa-play or fa-pause)
    const iconClass = await page.evaluate((sel) => {
      const btn = document.querySelector(sel);
      if (!btn) return null;
      const icon = btn.querySelector('i');
      return icon ? icon.className : null;
    }, playPauseBtnSelector);

    return {
      success: true,
      action: "Clicked the play/pause button in the first video container (data-video-id='1').",
      extractedData: {
        'playPauseButtonIconClass': iconClass
      },
      screenshots,
      error: null
    };

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
