  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container with data-video-id="1"
    const videoContainerSelector = '[data-video-id="1"]';
    await page.waitForSelector(videoContainerSelector);

    // Wait for the volume slider inside this container
    const volumeSliderSelector = `${videoContainerSelector} input.volume-slider`;
    await page.waitForSelector(volumeSliderSelector);

    // Set the volume slider to 0.5 (50%)
    await page.$eval(volumeSliderSelector, el => { el.value = 0.5; el.dispatchEvent(new Event('input', { bubbles: true })); el.dispatchEvent(new Event('change', { bubbles: true })); });

    // Wait a moment for UI to update if needed
    await page.waitForTimeout(500);

    // Take screenshot after setting volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Extract the value of the slider to verify
    const volumeValue = await page.$eval(volumeSliderSelector, el => el.value);

    return {
      success: true,
      action: "Set the first video's volume slider (data-video-id='1') to 50%",
      extractedData: {
        volumeValue: volumeValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

