    const volumeSliderHandle = await page.$(`${videoContainerSelector} input.volume-slider[type="range"]`);
    if (!volumeSliderHandle) {
      throw new Error('Volume slider for first video not found');
    }

    // Scroll the slider into view
    await volumeSliderHandle.evaluate(el => el.scrollIntoView({behavior: 'instant', block: 'center'}));

    // Set the volume value to 0.5 (50%)
