    await volumeSliderHandle.evaluate(slider => {
      slider.value = 0.5;
      slider.dispatchEvent(new Event('input', { bubbles: true }));
      slider.dispatchEvent(new Event('change', { bubbles: true }));
    });

    // Wait a little for any UI updates
    await page.waitForTimeout(500);

    // Take screenshot after setting volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Extract the value from the slider to verify
    const volumeValue = await volumeSliderHandle.evaluate(slider => slider.value);

    return {
      success: true,
