    // Extract the value from the slider to verify
    const volumeValue = await sliderHandle.evaluate(input => input.value);

    return {
      success: true,
      action: "Set the first video's volume slider to 50% (0.5) using direct selector and event dispatch.",
      extractedData: {
        volumeValue
      },
