import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Wait for the first video container with data-video-id="1"
    const videoContainerSelector = 'div.video-container[data-video-id="1"]';
    await page.waitForSelector(videoContainerSelector, { timeout: 5000 });

    // Wait for the play/pause button inside the first video container
    const playPauseBtnSelector = `${videoContainerSelector} button.play-pause-btn`;
    await page.waitForSelector(playPauseBtnSelector, { timeout: 5000 });

    // Take a screenshot before clicking
    const beforeClickScreenshot = path.join(screenshotsDir, 'step_2_before_click.png');
    await page.screenshot({ path: beforeClickScreenshot });
    screenshots.push(beforeClickScreenshot);

    // Click the play/pause button
    await page.click(playPauseBtnSelector);

    // Wait a short moment for UI update (icon change, etc.)
    await page.waitForTimeout(500);

    // Take a screenshot after clicking
    const afterClickScreenshot = path.join(screenshotsDir, 'step_3_after_click.png');
    await page.screenshot({ path: afterClickScreenshot });
    screenshots.push(afterClickScreenshot);

    // Extract the icon class inside the play/pause button to verify state change
    const iconClass = await page.$eval(
      `${playPauseBtnSelector} i`,
      el => el.className
    );

    return {
      success: true,
      action: "Clicked the play/pause button of the first video (data-video-id='1')",
      extractedData: {
        playPauseBtnIconClass: iconClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

