import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for the first video container with data-video-id="1" to appear
    const videoContainerSelector = 'div.video-container[data-video-id="1"]';
    await page.waitForSelector(videoContainerSelector, { timeout: 5000 });

    // Use a more specific selector for the play/pause button inside this container
    const playPauseBtnSelector = `${videoContainerSelector} button.play-pause-btn`;

    // Double-check the button is visible and enabled
    const playPauseBtn = await page.waitForSelector(playPauseBtnSelector, { timeout: 3000, state: 'visible' });

    // Take screenshot before clicking
    const step2 = path.join(screenshotsDir, 'step_2_before_click.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Click the play/pause button using a robust method (evaluateHandle to ensure correct element)
    await playPauseBtn.hover();
    await playPauseBtn.click({ delay: 100 });

    // Wait briefly for any UI update (e.g., icon change)
    await page.waitForTimeout(800);

    // Take screenshot after clicking
    const step3 = path.join(screenshotsDir, 'step_3_after_click.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract the play/pause button's icon class to determine if it toggled
    const btnIconClass = await page.evaluate((sel) => {
      const btn = document.querySelector(sel);
      if (!btn) return null;
      const icon = btn.querySelector('i');
      return icon ? icon.className : null;
    }, playPauseBtnSelector);

    return {
      success: true,
      action: "Clicked the play/pause button of the first video (data-video-id='1').",
      extractedData: {
        playPauseBtnIconClass: btnIconClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
