import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('div[data-video-id="1"]');

    // Wait for and click the like button within the first video container
    const likeButton = await videoContainer.waitForSelector('button.like-btn');
    await likeButton.click();

    // Wait for UI to update after click
    await page.waitForTimeout(1000);

    // Take screenshot after clicking like button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_like.png'));

    // Extract like count to verify the action
    const likeCount = await likeButton.textContent();

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1')",
      extractedData: {
        videoId: '1',
        likeCount: likeCount.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1'
    await page.waitForSelector('[data-video-id="1"]', { timeout: 5000 });

    // Wait for like button within the first video container
    const likeButton = await page.waitForSelector('[data-video-id="1"] .like-btn', { timeout: 5000 });

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the like button
    await likeButton.click();

    // Wait for state change (add a small delay for UI update)
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Verify the button state changed by checking for active class
    const isLiked = await page.evaluate(() => {
      const likeBtn = document.querySelector('[data-video-id="1"] .like-btn');
      return likeBtn.classList.contains('active') || 
             likeBtn.classList.contains('liked') ||
             likeBtn.getAttribute('data-liked') === 'true';
    });

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        liked: isLiked,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the like button of the first video (data-video-id='1')
    await page.waitForSelector('div[data-video-id="1"] button.like-btn', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_like.png'));

    // Click the like button using multiple selector strategies
    await page.click('div[data-video-id="1"] button.like-btn');

    // Wait for animation/state change and take after screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_like.png'));

    // Verify the like was successful by checking if the button state changed
    const likeState = await page.$eval('div[data-video-id="1"] button.like-btn', 
        btn => btn.classList.contains('liked') || btn.querySelector('i').classList.contains('fas'));

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        liked: likeState,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
