import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal.png'));

    // Wait for caption input field and enter text
    await page.waitForSelector('#videoCaption');
    await page.fill('#videoCaption', 'Amazing video!');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_filled.png'));

    // Verify the text was entered correctly
    const captionValue = await page.$eval('#videoCaption', el => el.value);

    return {
      success: true,
      action: "Entered 'Amazing video!' into the caption field of upload modal",
      extractedData: {
        captionText: captionValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the upload button
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for upload modal to appear and take screenshot
    await page.waitForSelector('.modal-content');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Wait for caption field and input text using multiple selector strategies
    const captionSelector = '#videoCaption';
    await page.waitForSelector(captionSelector);

    // Clear field and input text
    await page.click(captionSelector);
    await page.keyboard.press('Control+A');
    await page.keyboard.press('Backspace');
    await page.type(captionSelector, 'Amazing video!', { delay: 50 });

    // Take screenshot after input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_filled.png'));

    // Verify the input was successful
    const inputValue = await page.$eval(captionSelector, el => el.value);
    const extractedData = {
      captionText: inputValue,
      success: inputValue === 'Amazing video!'
    };

    return {
      success: true,
      action: "Successfully entered 'Amazing video!' into the caption field of the upload modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

