import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to load
    await page.waitForSelector('.video-container', { timeout: 5000 });

    // Extract data from all video containers
    const videoData = await page.$$eval('.video-container', (containers) => {
      return containers.map(container => {
        const likeBtn = container.querySelector('.like-btn span');
        const commentBtn = container.querySelector('.comment-btn span');

        return {
          likeCount: likeBtn ? likeBtn.textContent.trim() : 'N/A',
          commentCount: commentBtn ? commentBtn.textContent.trim() : 'N/A',
          videoId: container.getAttribute('data-video-id') || 'N/A'
        };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted like counts, comment counts, and video IDs from all video containers",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

