import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to be present
    await page.waitForSelector('.video-container', { timeout: 5000 });

    // Extract data from all video containers
    const videosData = await page.evaluate(() => {
      const containers = document.querySelectorAll('.video-container');
      const results = [];

      containers.forEach(container => {
        // Get like count
        const likeBtn = container.querySelector('.like-btn');
        const likeCount = likeBtn ? likeBtn.querySelector('span')?.textContent?.trim() : 'N/A';

        // Get comment count
        const commentBtn = container.querySelector('.comment-btn');
        const commentCount = commentBtn ? commentBtn.querySelector('span')?.textContent?.trim() : 'N/A';

        // Get video ID
        const videoId = container.getAttribute('data-video-id') || 'N/A';

        results.push({
          'like-count': likeCount,
          'comment-count': commentCount,
          'video-id': videoId
        });
      });

      return results;
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted like count, comment count and video-id from all video containers",
      extractedData: videosData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
