import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for sidebar to load
    await page.waitForSelector('.sidebar');

    // Click on Profile menu using data-page attribute selector
    const profileMenuItem = await page.waitForSelector('.sidebar-item[data-page="profile"]');
    await profileMenuItem.click();

    // Wait for profile page to load
    await page.waitForSelector('#profilePage.active', { timeout: 5000 });

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information to verify success
    const profileInfo = await page.evaluate(() => {
      const profilePage = document.getElementById('profilePage');
      return {
        isProfilePageActive: profilePage.classList.contains('active'),
        profileTitle: profilePage.querySelector('h1')?.textContent || '',
        profileUsername: profilePage.querySelector('.username')?.textContent || ''
      };
    });

    return {
      success: true,
      action: "Clicked Profile menu in sidebar and navigated to profile page",
      extractedData: profileInfo,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

