import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for sidebar to load and locate Profile menu item using data attribute
    await page.waitForSelector('.sidebar-item[data-page="profile"]');

    // Click the Profile menu item
    await page.click('.sidebar-item[data-page="profile"]');

    // Wait for page transition and verify navigation by checking active state
    await page.waitForSelector('.sidebar-item[data-page="profile"].active');

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract verification data
    const profileActive = await page.$eval('.sidebar-item[data-page="profile"]', 
        el => el.classList.contains('active'));
    const profileText = await page.$eval('.sidebar-item[data-page="profile"] span', 
        el => el.textContent.trim());

    return {
      success: true,
      action: "Clicked Profile menu item in sidebar and navigated to profile page",
      extractedData: {
        isProfileActive: profileActive,
        menuText: profileText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to navigate to profile page",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
