import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Discover tab in sidebar
    await page.waitForSelector('.sidebar-item[data-page="discover"]');
    await page.click('.sidebar-item[data-page="discover"]');
    await page.waitForTimeout(1000); // Wait for page transition

    // Wait for trending hashtags to load and take screenshot
    await page.waitForSelector('.hashtag.trending');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Click first trending hashtag
    const firstHashtag = await page.$('.hashtag.trending:first-child');
    const hashtagText = await firstHashtag.textContent();
    await firstHashtag.click();
    await page.waitForTimeout(1500); // Wait for navigation

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtag_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtag_clicked.png'));

    return {
      success: true,
      action: "Clicked first trending hashtag on Discover page",
      extractedData: {
        hashtag: hashtagText.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

