import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for caption field and type text
    await page.waitForSelector('#videoCaption');
    await page.fill('#videoCaption', 'Amazing video!');

    // Take screenshot of filled caption field
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_caption_filled.png'));

    // Extract caption value for verification
    const captionValue = await page.$eval('#videoCaption', el => el.value);

    return {
      success: true,
      action: "Typed 'Amazing video!' into video caption field",
      extractedData: {
        captionValue: captionValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

